mapper.select:
SELECT l.* FROM
(SELECT label_id FROM  linkis_cg_manager_label_service_instance WHERE service_instance=#{instance}) lsi
INNER JOIN linkis_cg_manager_label l ON l.id = lsi.label_id;

SELECT l.* FROM
(SELECT label_id from linkis_cg_manager_label_resource WHERE resource_id = #{persistenceResource.id}) lri
INNER JOIN linkis_cg_manager_label l ON l.id = lri.label_id;

select service_instance from linkis_cg_manager_label_service_instance where label_id in (

    #{labelId}

)

select * from linkis_cg_manager_label where id in (

    #{labelId}

)

select distinct username from linkis_cg_manager_label_user where label_id in (

    #{labelId}

)

select.foreach:
SELECT l.id AS 'id',l.label_value_size AS 'labelValueSize',l.label_key AS 'labelKey',l.label_value AS 'stringValue',si.instance,si.name AS 'applicationName' FROM linkis_cg_manager_label l, linkis_cg_manager_label_service_instance lsi,linkis_cg_manager_label_value_relation lvr,linkis_cg_manager_service_instance si WHERE l.id = lsi.label_id AND l.id = lvr.label_id AND lsi.service_instance = si.instance AND (lvr.label_value_key ,lvr.label_value_content) IN
(#{valueKey},#{valueContent})
GROUP BY l.id,si.instance
HAVING COUNT(1) = l.label_value_size and count(1) = ${value.size}
HAVING l.label_value_size >= COUNT(1) and count(1) >= ${value.size}
HAVING COUNT(1) > 0

mapper.select:
SELECT l.id AS 'id',l.label_key AS 'labelKey',l.label_value AS 'stringValue', l.label_value_size AS 'labelValueSize', lsi.service_instance AS 'instance' FROM ( SELECT service_instance,label_id FROM linkis_cg_manager_label_service_instance WHERE service_instance IN
(#{node.instance})
) lsi INNER JOIN linkis_cg_manager_label l ON l.id = lsi.label_id;

select tmp.id from ( SELECT l.id,l.label_value_size FROM linkis_cg_manager_label l ,linkis_cg_manager_label_resource lr ,linkis_cg_manager_label_value_relation lvr WHERE l.id = lr.label_id AND l.id = lvr.label_id AND (l.label_key,lvr.label_value_key,lvr.label_value_content) IN
#{labelKey},#{valueKey},#{valueContent}
GROUP BY l.id,lr.id HAVING COUNT(1) = l.label_value_size ) as tmp limit 1

