mapper.select:
SELECT l.* FROM linkis_cg_manager_label l ,linkis_cg_manager_label_value_relation lvr WHERE l.id = lvr.label_id AND (l.label_key,lvr.label_value_key,lvr.label_value_content) IN
#{key},#{valueKey},#{valueContent}
GROUP BY l.id HAVING COUNT(1) = l.label_value_size

select.foreach:
SELECT l.* FROM linkis_cg_manager_label l ,linkis_cg_manager_label_value_relation lvr WHERE l.id = lvr.label_id AND (lvr.label_value_key,lvr.label_value_content) IN
(#{valueKey},#{valueContent})
GROUP BY l.id
HAVING COUNT(1) = l.label_value_size and count(1) = ${value.size}
HAVING l.label_value_size >= COUNT(1) and count(1) >= ${value.size}
HAVING COUNT(1) > 0

SELECT l.*
FROM linkis_cg_manager_label l ,linkis_cg_manager_label_value_relation lvr
WHERE l.id = lvr.label_id
AND l.label_key = #{key}
AND (lvr.label_value_key,lvr.label_value_content) IN

    (#{valueKey},#{valueContent})

GROUP BY l.id

    HAVING COUNT(1) = l.label_value_size and count(1) = ${value.size}


    HAVING l.label_value_size >= COUNT(1) and count(1) >= ${value.size}


    HAVING COUNT(1) > 0

SELECT l.* FROM linkis_cg_manager_label l ,linkis_cg_manager_label_resource lr ,linkis_cg_manager_label_value_relation lvr WHERE l.id = lr.label_id AND l.id = lvr.label_id AND l.label_key = #{key} AND (lvr.label_value_key,lvr.label_value_content) IN
(#{valueKey},#{valueContent})
GROUP BY l.id,lr.resource_id
HAVING COUNT(1) = l.label_value_size and count(1) = ${value.size}
HAVING l.label_value_size >= COUNT(1) and count(1) >= ${value.size}
HAVING COUNT(1) > 0
