mapper.update:
UPDATE linkis_cg_ec_resource_info_record
service_instance = #{serviceInstance}, request_times = #{requestTimes}, used_times = #{usedTimes}, release_times = #{releaseTimes}, used_time = #{usedTime}, release_time = #{releaseTime}, used_resource = #{usedResource}, request_resource = #{requestResource}, released_resource = #{releasedResource}, ecm_instance = #{ecmInstance}, status = #{status},
WHERE id = #{id}

mapper.select:
select * from linkis_cg_ec_resource_info_record

     service_instance = #{instance}
     and create_user = #{username}

    and label_value like concat('%,',#{engineType},'%')
    and create_time BETWEEN  #{startDate} AND #{endDate}

ORDER BY linkis_cg_ec_resource_info_record.create_time DESC

select * from linkis_cg_ec_resource_info_record
where id in (

    select max(ecr.id) as id from linkis_cg_ec_resource_info_record ecr
    WHERE 1=1

        and ecr.service_instance in

            #{i}





        and SUBSTRING_INDEX(SUBSTRING_INDEX(ecr.label_value,',',-1),"-",1) in

            #{i}


    GROUP BY  ecr.service_instance
);
