select SDS.LOCATION  FROM SDS
inner join TBLS ON SDS.SD_ID = TBLS.SD_ID
inner join `DBS` ON TBLS.DB_ID=`DBS`.DB_ID
where  TBLS.TBL_NAME = #{tableName,jdbcType=VARCHAR}
and `DBS`.NAME = #{dbName,jdbcType=VARCHAR};

