SELECT c.COMMENT, c.COLUMN_NAME, c.TYPE_NAME
FROM COLUMNS_V2 c
INNER JOIN SDS s ON c.CD_ID = s.CD_ID
INNER JOIN TBLS t ON s.SD_ID = t.SD_ID
INNER JOIN DBS d ON t.DB_ID = d.DB_ID
WHERE t.TBL_NAME = #{tableName,jdbcType=VARCHAR}
AND d.NAME = #{dbName,jdbcType=VARCHAR}
ORDER BY c.INTEGER_IDX ASC;

/*slave*/ SELECT c.COMMENT, c.COLUMN_NAME, c.TYPE_NAME
FROM COLUMNS_V2 c
INNER JOIN SDS s ON c.CD_ID = s.CD_ID
INNER JOIN TBLS t ON s.SD_ID = t.SD_ID
INNER JOIN DBS d ON t.DB_ID = d.DB_ID
WHERE t.TBL_NAME = #{tableName,jdbcType=VARCHAR}
AND d.NAME = #{dbName,jdbcType=VARCHAR}
ORDER BY c.INTEGER_IDX ASC;

