/*slave*/ SELECT * FROM linkis_ps_job_history_group_history job

    JOIN linkis_org_user dept ON job.submit_user = dept.user_name


    job.id = #{id}
    and job.submit_user = #{umUser}
    and job.engine_type = #{engineType}
    and job.created_time >= #{startDate} AND job.created_time <= #{endDate}
    and #{element}
    and job.id >= #{startId}
    and LOWER(job.instances) = LOWER(#{instances})
    and dept.org_id = #{departmentId}
    and job.metrics LIKE CONCAT('%"engineInstance":"', #{engineInstance}, '%')
    and job.labels LIKE CONCAT('%"codeType":"', #{runType}, '%')

ORDER BY job.created_time DESC

/*slave*/ SELECT id FROM linkis_ps_job_history_group_history

    and created_time >= #{startDate} AND created_time <= #{endDate}
    and id >= #{startId}
    and #{element}

limit 2

/*slave*/ SELECT id FROM linkis_ps_job_history_group_history

    and created_time >= #{startDate} AND created_time <= #{endDate}
    and id >= #{startId}

ORDER BY linkis_ps_job_history_group_history.created_time

/*slave*/ SELECT * FROM linkis_ps_job_history_group_history job

    JOIN linkis_org_user dept ON job.submit_user = dept.user_name


    job.id = #{id}
    and job.submit_user = #{umUser}
    and job.engine_type = #{engineType}
    and job.created_time >= #{startDate} AND job.created_time <= #{endDate}
    and #{element}

        and  LOCATE(CONCAT('"', #{userCreatorKey}, '":"', #{userCreatorValue}), job.labels) > 0

    and job.id >= #{startId}
    and LOWER(job.instances) = LOWER(#{instances})
    and dept.org_id = #{departmentId}
    and job.metrics LIKE CONCAT('%"engineInstance":"', #{engineInstance}, '%')
    and job.labels LIKE CONCAT('%"codeType":"', #{runType}, '%')

ORDER BY job.created_time DESC

/*slave*/ SELECT * FROM linkis_ps_job_history_group_history job

    JOIN linkis_org_user dept ON job.submit_user = dept.user_name


    job.id = #{id}
    and job.submit_user = #{umUser}
    and job.engine_type = #{engineType}
    and job.created_time >= #{startDate} AND job.created_time <= #{endDate}
    and #{element}

        and job.labels like CONCAT('%"', #{userCreatorKey}, '":"%-', #{creator}, '%')

    and job.id >= #{startId}
    and LOWER(job.instances) = LOWER(#{instances})
    and dept.org_id = #{departmentId}
    and job.metrics LIKE CONCAT('%"engineInstance":"', #{engineInstance}, '%')
    and job.labels LIKE CONCAT('%"codeType":"', #{runType}, '%')

ORDER BY job.created_time DESC

mapper.update:
UPDATE linkis_ps_job_history_group_history
updated_time = #{updateTimeMills}, job_req_id = #{jobReqId}, submit_user = #{submitUser}, execute_user = #{executeUser}, `source` = #{source}, labels = #{labels}, params = #{params}, progress = #{progress}, status = #{status}, log_path = #{logPath}, result_location=#{resultLocation}, error_code = #{errorCode}, error_desc = #{errorDesc}, created_time = #{createdTime}, instances = #{instances}, metrics = #{metrics}, engine_type = #{engineType}, execution_code = #{executionCode}, observe_info = #{observeInfo},
WHERE id = #{id} AND updated_time <= #{updateTimeMills}

