mapper.select:
/*slave*/ SELECT * FROM linkis_ps_job_history_group_history
WHERE id IN

    #{id}

and submit_user = #{umUser}
ORDER BY linkis_ps_job_history_group_history.created_time DESC

SELECT a.* FROM linkis_ps_job_history_group_history a
WHERE (
a.instances = ''
OR a.instances IS NULL
OR a.instances NOT IN #{key}
OR EXISTS (
SELECT 1 FROM
(

    SELECT #{key} AS instances, #{val} AS registryTime

) b
WHERE a.instances = b.instances AND a.created_time < FROM_UNIXTIME(b.registryTime/1000)
)
)
AND
status IN #{status}
AND a.created_time >= FROM_UNIXTIME(#{startTimestamp}/1000)
LIMIT #{limit}

/*slave*/ SELECT * FROM linkis_ps_job_history_group_history

    and submit_user = #{umUser}
    and engine_type = #{engineType}
    and created_time >= #{startDate} AND created_time <= #{endDate}

ORDER BY FROM_UNIXTIME(TIMEDIFF(cast(updated_time as datetime), cast(created_time as datetime))) desc

