mapper.select:
SELECT * FROM linkis_ps_python_module_info WHERE 1=1

    AND name LIKE CONCAT('%', #{name}, '%')


    AND create_user LIKE CONCAT('%', #{createUser}, '%')


    AND engine_type = #{engineType}


    AND is_expire = #{isExpire}


    AND is_load = #{isLoad}

ORDER BY create_time DESC

mapper.update:
UPDATE linkis_ps_python_module_info
name = #{name}, description = #{description}, path = #{path}, engine_type = #{engineType}, create_user = #{createUser}, update_user = #{updateUser}, is_load = #{isLoad}, is_expire = #{isExpire}, create_time = #{createTime}, update_time = #{updateTime}, python_module = #{pythonModule},
WHERE id = #{id}

mapper.select:
SELECT * FROM linkis_ps_python_module_info WHERE 1=1
AND create_user = #{createUser}
AND name = #{name}
AND  id = #{id}
AND  is_load = #{isLoad}

SELECT * FROM linkis_ps_python_module_info
WHERE create_user = #{username}
AND engine_type IN

    #{engineType}

AND is_expire = 0
AND is_load = 1
